drop table if exists categories;

CREATE TABLE siteinfo(
	site_id				INTEGER,
	site_title		TEXT,
	site_width		INTEGER,
	cust_scroll		INTEGER,
	site_theme		TEXT,
	site_headline	TEXT
);

CREATE TABLE categories(
	cat_id				INTEGER PRIMARY KEY AUTOINCREMENT,
	cat_title			INTEGER,
	cat_mtime			INTEGER,
	cat_content		TEXT,
	cat_pos			INTEGER,
	cat_visible		INTEGER
);

